create table personalities(
    id serial primary key,
    name varchar,
    history varchar
);

INSERT INTO personalities(name, history) VALUES
('Filipe Camarão', 'Antônio Filipe Camarão,[1] nascido Poti ou Potiguaçu[2] (Nordeste do Brasil c. 1600/1601 – Recife, 24 de agosto de 1648), foi um militar de nacionalidade portuguesa e de naturalidade brasileira, a serviço do Império Português, tendo sido também um líder indígena. Chefe nativo dos índios potiguares, foi um dos heróis da Batalha dos Guararapes, episódio decisivo da Insurreição Pernambucana. Recebeu o título de Capitão-Mor de Todos os Índios do Brasil.'),
('Henrique Dias', 'Henrique Dias (Capitania de Pernambuco, princípios do século XVII – Recife, 7 ou 8 de junho de 1662) foi um militar nascido no Brasil colonial, filho de escravizados de África libertos. Foi um dos heróis da Batalha dos Guararapes, episódio decisivo da Insurreição Pernambucana (expulsão dos holandeses do Nordeste do Brasil).');